mapper.insert:

	SELECT NVL(MAX(department_id), 10) + 1
	FROM dept

INSERT INTO dept(
department_id
, department_name
, manager_id
, location_id
)VALUES(
#{departmentId}
, #{departmentName}
, #{managerId}
, #{locationId}
)

mapper.update:
UPDATE dept
SET department_name = #{dept.departmentName}
, manager_id = #{dept.managerId}
, location_id = #{dept.locationId}
WHERE department_id = #{deptId}
